import {Component, EventEmitter, Input, Output} from '@angular/core';
import {MainPageComponent} from "../main-page/main-page.component";

@Component({
  selector: 'app-warning-box',
  templateUrl: './warning-box.component.html',
  styleUrls: ['./warning-box.component.css']
})
export class WarningBoxComponent {

}
<div class="warning-box">
    <div class="warning-message">
        <button class = "x-button">X</button>
        Do you want to response?
        <button class="response-button, cancel" >Cancel</button>
        <button class="response-button" >Response</button>
    </div>
</div>
