import {Component, OnInit} from '@angular/core';
import {Vacancy} from "../models";
import {ActivatedRoute} from "@angular/router";
import {VacanciesService} from "../vacancies.service";

@Component({
  selector: 'app-my-vacancies-employer',
  templateUrl: './my-vacancies-employer.component.html',
  styleUrls: ['./my-vacancies-employer.component.css']
})
export class MyVacanciesEmployerComponent  implements OnInit {
  vacancies: Vacancy[];
  company_id: number

  constructor(
      private route: ActivatedRoute,
      private service: VacanciesService
  ) {
    this.vacancies = [];
    this.company_id = 0;
  }

  ngOnInit() {
    this.company_id = Number(this.route.snapshot.paramMap.get('id'));
    this.getVacancies(this.company_id)
  }

  getVacancies(id: number) {
    this.service.getCompanyVacancies(id).subscribe((data) => {
      this.vacancies = data
      console.log(this.vacancies.length)
    })
  }
}
