import {Component, OnChanges, OnInit} from '@angular/core';
import {Vacancy} from "../models";
import {ActivatedRoute} from "@angular/router";
import {VacanciesService} from "../vacancies.service";
import {HttpClient} from "@angular/common/http";

@Component({
  selector: 'app-main-page',
  templateUrl: './main-page.component.html',
  styleUrls: ['./main-page.component.css']
})
export class MainPageComponent implements OnInit{
  vacancies: Vacancy[];
  query: string;
  constructor(private route: ActivatedRoute, private service: VacanciesService, private client: HttpClient) {
    this.vacancies = [];
    this.query = '';
  }

  search() {
    const params = this.query;
    this.client.get<any[]>('/api/search/vacancies/', { params }).subscribe(
        data => this.results = data,
        error => console.error(error)
    );
  }

  ngOnInit(){
    this.getAllVacancies()
  }

  getAllVacancies() {
    this.service.getVacancies().subscribe((data) => (this.vacancies = data));
  }

  searchVacancies(query: String) {
    this.service.getSearchVacancies(this.query).subscribe(data => this.vacancies = data);
  }
}
